import axios from 'axios';

const apiUrl = "http://localhost:5165"; // HTTPS -> HTTP כדי להימנע משגיאות Mixed Content

export default {
  getTasks: async () => {
    try {
      const result = await axios.get(`${apiUrl}/tasks`);
      return Array.isArray(result.data) ? result.data : [];  // החזרת מערך ריק במקרה של שגיאה
    } catch (error) {
      console.error("Error fetching tasks:", error);
      return [];
    }
  },

  addTask: async (name) => {
    try {
      const newTask = { name, isComplete: false };
      const result = await axios.post(`${apiUrl}/tasks`, newTask, {
        headers: { "Content-Type": "application/json" },
      });
      return result.data;
    } catch (error) {
      console.error("Error adding task:", error);
      return null;
    }
  },

  setCompleted: async (id, isComplete) => {
    try {
      const result = await axios.put(`${apiUrl}/tasks/${id}`, { id, isComplete }, {
        headers: { "Content-Type": "application/json" },
      });
      return result.data;
    } catch (error) {
      console.error("Error updating task:", error);
      return null;
    }
  },

  deleteTask: async (id) => {
    try {
      await axios.delete(`${apiUrl}/tasks/${id}`);
      return true;
    } catch (error) {
      console.error("Error deleting task:", error);
      return false;
    }
  }
};
